(1197, 1189, 'product', 'Trúc ', '695898796879687', 1170, 'Bếp từ 3 bếp Bosch PID675N24E', 'bep.vn/bep-tu-3-bep-bosch-pid675n24e.html', '2014-06-17 20:01:55', 1, 'Dùng được. Dễ dùng lắm. Bosch PID675N24E có nhiều bếp, nồi to nhỏ, nấu được hết. ', 0, NULL),
(1198, 107, 'product', 'Ý ', '58695896879687', 1170, 'Bếp từ 3 bếp Bosch PID675N24E', 'bep.vn/bep-tu-3-bep-bosch-pid675n24e.html', '2014-06-17 20:03:29', 1, '<rep>@Trà </rep>Mình cũng thấy Bosch PID675N24E và Bosch PID651N24E giống nhau nhưng sao lại là 2 tên khác nhau nhỉ? Chắc họ chụp ảnh nhầm. Qua cửa hàng xem cho chính xác bạn ạ. ', 0, 'Trà '),
(1199, 1159, 'product', 'Thanh ', '8476586758675867', 1973, 'Bếp từ 3 bếp Bosch PID651N24E', 'bep.vn/bep-tu-3-bep-bosch-pid651n24e.html', '2014-06-17 20:04:43', 1, '<rep>@Triệu </rep>Nhà mình dùng bếp từ cũng nhàn thật, nấu nhanh lắm, mùa này lại không bị nóng nữa. Không ân hận khi quyết định mua. ', 0, 'Triệu '),
(1200, 1191, 'product', 'Tiến ', '46785768567857', 1973, 'Bếp từ 3 bếp Bosch PID651N24E', 'bep.vn/bep-tu-3-bep-bosch-pid651n24e.html', '2014-06-17 20:05:35', 1, 'Gần vuông. Hai chiều chênh nhau xíu. Bếp từ giờ nhìn đẹp ghê. ', 0, NULL),
(1201, 391, 'product', 'Doanh ', '487685768567', 1973, 'Bếp từ 3 bếp Bosch PID651N24E', 'bep.vn/bep-tu-3-bep-bosch-pid651n24e.html', '2014-06-17 20:06:24', 0, 'Sợ đắt thì khó mua bếp lắm. Giờ bếp từ cứ phải 2, 3 chục củ. ', 0, NULL),
(1202, 1193, 'product', 'Hân ', '867856786787', 1429, 'Bếp từ 2 bếp Bosch PMI968MS', 'bep.vn/bep-tu-2-bep-bosch-pmi968ms.html', '2014-06-17 23:55:30', 0, 'Vậy thì mình cũng không mua Bosch PMI968MS nữa nhưng cho mình hỏi là hàng giả hay hàng tàu bạn nhỉ? ', 0, NULL),
(1203, 0, 'product', 'Khuyên ', '8467586758658678', 1429, 'Bếp từ 2 bếp Bosch PMI968MS', 'bep.vn/bep-tu-2-bep-bosch-pmi968ms.html', '2014-06-17 23:56:47', 0, 'Chết rồi, nhà tôi vừa lắp Bosch PMI968MS, coi như phí tiền. Sợ nhất là tai nạn ấy. Chắc cũng phải cố thay thôi. ', 0, NULL),
