(1441, 0, 'product', 'Hoàng Chương ', '4858695689586', 1170, 'Bếp từ 3 bếp Bosch PID675N24E', 'bep.vn/bep-tu-3-bep-bosch-pid675n24e.html', '2014-06-28 02:58:46', 0, 'Bosch PID675N24E hàng ngoại chắc không lo về chất lượng đâu anh em nhỉ? ', 0, NULL),
(1442, 0, 'product', 'Diễm Phương ', '8578796879687', 1170, 'Bếp từ 3 bếp Bosch PID675N24E', 'bep.vn/bep-tu-3-bep-bosch-pid675n24e.html', '2014-06-28 02:59:48', 0, 'Biết bếp từ tốt như này thì năm ngoái mình mua cho rồi. Hồi ấy chẳng thấy ai nói gì. Đến là khổ. ', 0, NULL),
(1443, 1411, 'product', 'Thương Anh ', '847685768567', 1973, 'Bếp từ 3 bếp Bosch PID651N24E', 'bep.vn/bep-tu-3-bep-bosch-pid651n24e.html', '2014-06-28 03:02:33', 0, 'Tớ dùng nửa năm rồi, tiết kiệm, tiết bằng 1/3 ấy. ', 0, NULL),
(1444, 1424, 'product', 'Minh Anh ', '857857658675867', 1973, 'Bếp từ 3 bếp Bosch PID651N24E', 'bep.vn/bep-tu-3-bep-bosch-pid651n24e.html', '2014-06-28 03:05:02', 0, 'Vừa làm một vòng khảo giá, bếp ga, bếp từ giá tương đương. Dùng thì đọc thấy khác mà, với lại bếp ga thấy đỡ nguy hiểm hơn. ', 0, NULL),
(1445, 1424, 'product', 'Linh Anh ', '8567878787', 1973, 'Bếp từ 3 bếp Bosch PID651N24E', 'bep.vn/bep-tu-3-bep-bosch-pid651n24e.html', '2014-06-28 03:06:28', 0, 'Hai loại bếp hoàn toàn khác nhau, bếp từ là bếp từ, bếp ga là bếp ga, "vậy" sao được. Nó phải khác thì người ta mới phải cất công sáng tạo, thiết kế chứ. ', 0, NULL),
(1446, 0, 'product', 'Tuấn Anh ', '8576857685678', 1973, 'Bếp từ 3 bếp Bosch PID651N24E', 'bep.vn/bep-tu-3-bep-bosch-pid651n24e.html', '2014-06-28 03:07:54', 0, 'Em thì chưa dùng, vào đọc hoa hết cả mắt, mỗi người một ý kiến. Cuối cùng thì nên mua bếp nào, ga hay từ nhỉ? ', 0, NULL),
(1447, 0, 'product', 'Nguyện', '8568568567867', 1429, 'Bếp từ 2 bếp Bosch PMI968MS', 'bep.vn/bep-tu-2-bep-bosch-pmi968ms.html', '2014-06-28 03:10:12', 0, 'Nhà mình đang phanh các bạn hàng tàu rồi, tất cả, không chỉ bếp biếc đâu. Nghĩ đi nghĩ lại sao ở đâu cũng thấy hàng tàu, tưởng bếp từ  thì không, hóa ra .... ', 0, NULL),
(1448, 0, 'product', 'Hà Nam ', '8768576876', 1429, 'Bếp từ 2 bếp Bosch PMI968MS', 'bep.vn/bep-tu-2-bep-bosch-pmi968ms.html', '2014-06-28 03:11:55', 1, 'Bà nhà em cứ khăng khăng  phải đi mua bếp từ, chỉ vì bà bạn hàng xóm "đang dùng thích lắm". Thực ra, nhà em cũng chưa phải thay bếp nhưng thôi, cố vậy. Bác nào có kinh nghiệm chọn bếp từ, tư vấn em với. ', 0, NULL),
(1449, 0, 'product', 'Hoàng Lan ', '857685686787', 1429, 'Bếp từ 2 bếp Bosch PMI968MS', 'bep.vn/bep-tu-2-bep-bosch-pmi968ms.html', '2014-06-28 03:13:11', 1, 'Cả buổi chiều hôm nay lượng mà không chọn được em bếp từ nào ưng, không biết mai đến bep.vn có khá khẩm hơn không? ', 1, NULL),
(1450, 0, 'product', 'Nhu ', '85768576867', 1429, 'Bếp từ 2 bếp Bosch PMI968MS', 'bep.vn/bep-tu-2-bep-bosch-pmi968ms.html', '2014-06-28 03:14:41', 0, 'Kiểu này thì dùng bếp than cho lành, chứ bếp từ tàu thì các tiền cũng chịu. ', 0, NULL),
(1451, 0, 'product', 'Hiệp ', '87685768576', 1170, 'Bếp từ 3 bếp Bosch PID675N24E', 'bep.vn/bep-tu-3-bep-bosch-pid675n24e.html', '2014-06-28 23:26:45', 0, 'Bosch PID675N24E nhà em kêu to quá anh em ạ. Mấy hôm nay thức xem WC, cứ định đi nấu bát mì tôm ăn mà lại sợ mất trật tự, bị bà xã kêu, lại thôi. Xử lý cái này như nào đây? ', 0, NULL),
(1452, 0, 'product', 'Thắng ', '857685768576', 1170, 'Bếp từ 3 bếp Bosch PID675N24E', 'bep.vn/bep-tu-3-bep-bosch-pid675n24e.html', '2014-06-28 23:27:33', 0, 'Hình như quạt gió Bosch PID675N24E nhà em hỏng, có bác nào biết thay bao nhiêu tiền không? ', 0, NULL),
(1453, 0, 'product', 'Đình ', '876857685768576', 1170, 'Bếp từ 3 bếp Bosch PID675N24E', 'bep.vn/bep-tu-3-bep-bosch-pid675n24e.html', '2014-06-28 23:28:28', 0, 'Dập dình mua Bosch PID675N24E mấy lần mà không được, lúc thì không đủ tiền, lúc thì bep.vn hết hàng. Vô duyên thế chứ. ', 0, NULL),
(1454, 0, 'product', 'Sang ', '857685768576', 1170, 'Bếp từ 3 bếp Bosch PID675N24E', 'bep.vn/bep-tu-3-bep-bosch-pid675n24e.html', '2014-06-28 23:29:44', 0, 'Nhà chị bạn mình cãi nhau vì bếp từ, chồng thì khăng khăng không cần mua, vợ thì đòi mua bằng được. Ghê thật. ', 0, NULL),
(1455, 0, 'product', 'Nghĩa ', '87685768576857', 1170, 'Bếp từ 3 bếp Bosch PID675N24E', 'bep.vn/bep-tu-3-bep-bosch-pid675n24e.html', '2014-06-28 23:30:44', 0, 'Thấy mấy vụ bếp từ giả vừa đọc trên báo mà rùng mình, làm thế nào biết đâu là giả đâu là thật nhỉ? ', 0, NULL),
(1456, 0, 'product', 'Cường ', '8578768796879', 1170, 'Bếp từ 3 bếp Bosch PID675N24E', 'bep.vn/bep-tu-3-bep-bosch-pid675n24e.html', '2014-06-28 23:31:51', 0, 'Càng dùng Bosch PID675N24E càng thấy yên tâm, trình nấu ăn của mình lên hẳn, cũng một phần nhờ bếp nay nấu dễ. ', 0, NULL),
(1457, 0, 'product', 'Đồng ', '8785768576', 1973, 'Bếp từ 3 bếp Bosch PID651N24E', 'bep.vn/bep-tu-3-bep-bosch-pid651n24e.html', '2014-06-28 23:33:17', 0, 'Vừa lắp Bosch PID651N24E tối nay, hy vọng dùng ổn. ', 0, NULL),
(1458, 0, 'product', 'Thành ', '8768576857687', 1973, 'Bếp từ 3 bếp Bosch PID651N24E', 'bep.vn/bep-tu-3-bep-bosch-pid651n24e.html', '2014-06-28 23:34:08', 0, 'Có chỗ nào bá bếp từ rẻ hơn bep.vn không các bác. Vừa dạo một vòng, em nào cũng giá cắt cổ. ', 0, NULL),
(1459, 0, 'product', 'Mãi ', '8576857685768', 1973, 'Bếp từ 3 bếp Bosch PID651N24E', 'bep.vn/bep-tu-3-bep-bosch-pid651n24e.html', '2014-06-28 23:34:54', 0, 'Mẹ em mê bếp từ cả năm nay mà chưa có điều kiện thay cho cụ, xem bep.vn có em nào vừa vừa tiền không nào.', 0, NULL),
(1460, 0, 'product', 'Cẩn ', '85768576867', 1973, 'Bếp từ 3 bếp Bosch PID651N24E', 'bep.vn/bep-tu-3-bep-bosch-pid651n24e.html', '2014-06-28 23:36:45', 0, 'Nghỉ trưa được hơn tiếng đồng hồ thì bị vợ bắt đi xem bếp từ đến cả tiếng. Mệt quá. Mà đã mua được đâu. Bác nào có mánh chọn bếp như nào chỉ em với. Đi xem hoa hết cả mắt, chẳng chọn được. ', 0, NULL),
(1461, 0, 'product', 'Chức ', '8768576857687', 1973, 'Bếp từ 3 bếp Bosch PID651N24E', 'bep.vn/bep-tu-3-bep-bosch-pid651n24e.html', '2014-06-28 23:38:16', 0, 'Bếp từ vẫn là hàng xa xỉ với mình. Cũng tiết kiệm được một khoản nhưng giờ mua là hết. Nhịn cơn thèm vậy. ', 0, NULL),
(1462, 0, 'product', 'Hiền ', '857686787', 1973, 'Bếp từ 3 bếp Bosch PID651N24E', 'bep.vn/bep-tu-3-bep-bosch-pid651n24e.html', '2014-06-28 23:39:09', 0, 'Bác nào mua lại Bosch PID651N24E không, em bán rẻ cho, mới dùng được 1 năm, nhà em đang cần tiền. ', 0, NULL),
(1463, 0, 'product', 'Đạo ', '87685768576', 1973, 'Bếp từ 3 bếp Bosch PID651N24E', 'bep.vn/bep-tu-3-bep-bosch-pid651n24e.html', '2014-06-28 23:40:39', 0, 'Nhà mình vừa hoàn thiện, giờ đến sắm nội thất, đau đầu. Bep.vn có tư vấn lắp bếp không? ', 0, NULL),
(1464, 0, 'product', 'Lợi ', '87685768576', 1973, 'Bếp từ 3 bếp Bosch PID651N24E', 'bep.vn/bep-tu-3-bep-bosch-pid651n24e.html', '2014-06-28 23:41:37', 0, 'Mỗi lần bật Bosch PID651N24E, nó lại xè xè một vài dây mới im, bác nào có kinh nghiệm, tư vấn em xem có sao không với! ', 0, NULL),
(1465, 0, 'product', 'Bàn ', '85768576876867', 1973, 'Bếp từ 3 bếp Bosch PID651N24E', 'bep.vn/bep-tu-3-bep-bosch-pid651n24e.html', '2014-06-28 23:42:58', 0, 'Nhà em dùng Bosch PID651N24E mới được hơn 3 tháng mà phải thay dây đến 2 lần, các chú sửa bảo là chuột cắn dây. Không hiểu chuột cắn hay là do lỗi sản xuất. ', 0, NULL),
(1466, 0, 'product', 'Tình ', '8768568968796', 1973, 'Bếp từ 3 bếp Bosch PID651N24E', 'bep.vn/bep-tu-3-bep-bosch-pid651n24e.html', '2014-06-28 23:44:02', 0, 'Mình muốn có nhiều ô nấu hơn. Bep.vn có em nào của Bosch nhiều bếp nấu hơn không? ', 0, NULL),
(1467, 0, 'product', 'Vững ', '8569879676', 1973, 'Bếp từ 3 bếp Bosch PID651N24E', 'bep.vn/bep-tu-3-bep-bosch-pid651n24e.html', '2014-06-28 23:45:43', 0, 'Ngày nào cũng đều đặn vào bếp 3 lần, sáng, trưa, tối; hồi chưa có Bosch PID651N24E, mình phải mất đến 5 tiếng đấy nhưng giờ thì nhàn rồi, nấu nhanh hơn hẳn. ', 0, NULL),
(1468, 1448, 'product', 'Chấp ', '846869586', 1429, 'Bếp từ 2 bếp Bosch PMI968MS', 'bep.vn/bep-tu-2-bep-bosch-pmi968ms.html', '2014-06-28 23:49:30', 0, 'Cứ lựa em nào vừa tiền thôi bác ạ. Bếp từ giờ nhiều loại, chọn cũng dễ. ', 0, NULL),
(1469, 1448, 'product', 'Cuộc ', '8475869586968', 1429, 'Bếp từ 2 bếp Bosch PMI968MS', 'bep.vn/bep-tu-2-bep-bosch-pmi968ms.html', '2014-06-28 23:50:53', 0, 'Bà dùng rồi, đã có sự so sánh thì cứ yên tâm mua thôi. Chọn thì phải ra cửa hàng, họ tư vấn cho dễ bạn ạ. Tốt nhất là mua đúng loại bà thích ấy. ', 0, NULL),
(1470, 1448, 'product', 'Quan ', '846586857685', 1429, 'Bếp từ 2 bếp Bosch PMI968MS', 'bep.vn/bep-tu-2-bep-bosch-pmi968ms.html', '2014-06-28 23:52:42', 0, 'Bà nhà mình hồi đầu dùng bếp từ là phải ... đeo kính, giờ quen rồi mới thôi đấy. Bếp thì ở đâu cũng thế nhưng nhớ là phải mua chính hãng, như em Bosch pmi968ms là hàng nhái, đang bị chê đấy. ', 0, NULL),
(1471, 1449, 'product', 'Nhàn ', '48598659685986', 1429, 'Bếp từ 2 bếp Bosch PMI968MS', 'bep.vn/bep-tu-2-bep-bosch-pmi968ms.html', '2014-06-28 23:54:49', 0, 'Mua được hay không còn phải duyên nữa cơ, nhưng tư vấn của cửa hàng quan trọng phết. Nhiều em bán hàng như đuổi khách đi, chẳng giới thiệu giới thủng gì hết thì đố mà mua được. ', 0, NULL),
(1472, 0, 'product', 'Bài ', '8476859689586', 1429, 'Bếp từ 2 bếp Bosch PMI968MS', 'bep.vn/bep-tu-2-bep-bosch-pmi968ms.html', '2014-06-28 23:55:43', 0, 'Thôi, không mua Bosch pmi968ms nữa các bác nhỉ. Em vừa vào xem em này đã "phát sốt". ', 0, NULL),
(1473, 0, 'product', 'Dụng ', '8474869586958695', 1429, 'Bếp từ 2 bếp Bosch PMI968MS', 'bep.vn/bep-tu-2-bep-bosch-pmi968ms.html', '2014-06-28 23:56:44', 0, 'Mua bếp là phải cẩn thận các bác ạ. Mua phải em như Bosch pmi968ms thì phí tiền. ', 0, NULL),
(1474, 0, 'product', 'Quảng ', '847875865876', 1429, 'Bếp từ 2 bếp Bosch PMI968MS', 'bep.vn/bep-tu-2-bep-bosch-pmi968ms.html', '2014-06-28 23:57:48', 0, 'Nhà em đang muốn thanh lý em Bosch pmi968ms, bác nào mua dùng tạm, em để lại với giá cực mềm nhé. ', 0, NULL),
(1475, 0, 'product', 'Nhạc ', '847847857', 1429, 'Bếp từ 2 bếp Bosch PMI968MS', 'bep.vn/bep-tu-2-bep-bosch-pmi968ms.html', '2014-06-28 23:59:22', 0, 'Tớ muốn mua bếp từ dưới chục triệu, bạn nào có thông tin, cho mình nhé. ', 0, NULL);
